<?phpnamespace Braind\Photoload;use Bitrix\Main\Config\Option;class EventHandler{    public static function PhotoImportAdminContextMenuShow(&$items) {        global $APPLICATION;        $module_id = pathinfo(dirname(__DIR__))["basename"];        $iblock_id = Option::get($module_id, "iblock_id");        $access = (($APPLICATION->GetUserRight($module_id) >= "W") || ($APPLICATION->GetUserRight($module_id) == "MP"));        if ($_SERVER["REQUEST_METHOD"] == 'GET'            && $GLOBALS['APPLICATION']->GetCurPage() == '/bitrix/admin/iblock_section_admin.php'            && $_GET["IBLOCK_ID"] == $iblock_id            && $_GET["SECTION_ID"] > 0            && $access) {            $items[] = [                "TEXT" => "Импорт фотографий",                "ICON" => "",                "TITLE" => "Из директории на сервере",                //"LINK" => "/bitrix/admin/braind_photoload_script.php?section_id=".$_GET["SECTION_ID"]                "LINK" => "/local/modules/" . $module_id . "/admin/photoload_script.php?section_id=".$_GET["SECTION_ID"]            ];        }    }}